import React, {Component} from 'react';

import './Formulaire.css';

class Formulaire extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: "",
      nom: "",
      prenom: "",
      message: ""
    }
  }

  gerer_formulaire(event) {
    let cible = event.target;
    let valeur = cible.value.trim();
    let nom = cible.name;
    this.setState({[nom]: valeur});
  }

  render() {
    return (<div className="formulaire">

      <div className="field">
        <label className="label">Pr√©nom</label>
        <div className="control">
          <input className="input" type="text" placeholder="Text input"/>
        </div>
      </div>

      <div className="field">
        <label className="label">Nom</label>
        <div className="control">
          <input className="input" name="nom" type="text" placeholder="Text input"/>
        </div>
      </div>

      <div className="field">
        <label className="label">Email</label>
        <div className="control has-icons-left has-icons-right">
          <input className="input is-danger" name="email" type="email" placeholder="Email input" value="hello@"/>
          <span className="icon is-small is-left">
            <i className="fas fa-envelope"></i>
          </span>
          <span className="icon is-small is-right">
            <i className="fas fa-exclamation-triangle"></i>
          </span>
        </div>
        <p className="help is-danger">Cet email est invalide</p>
      </div>

      <div className="field">
        <label className="label">Message</label>
        <div className="control">
          <textarea className="textarea" name="message" placeholder="Textarea"></textarea>
        </div>
      </div>

      <div className="field">
        <div className="control">
          <button className="button is-link">Valider</button>
        </div>
      </div>
    </div>);
  }
}

export default Formulaire;
