import React, {Component} from 'react';

import './Moi.css';

class Moi extends Component {

  sleep(seconds){
    var waitUntil = new Date().getTime() + seconds*1000;
    i = 0;
    while(new Date().getTime() < waitUntil) {
      i++;
    }
  }

  typeWriter(s, index) {
    let min = Math.ceil(300);
    let max = Math.floor(500);
    let vitesse = Math.floor(Math.random() * (max - min)) + min;
    if (index < s.length) {
      document.querySelector('.type-writer').innerHTML += '|';

      document.querySelector('.type-writer').innerHTML = document.querySelector('.type-writer').innerHTML.substring(0,document.querySelector('.type-writer').innerHTML.length-1);
      document.querySelector('.type-writer').innerHTML += s.charAt(index);
      index++;
      setTimeout(() => {
        this.typeWriter(s, index, vitesse);
      }, vitesse);
    }
  }

  componentDidMount() {
    this.typeWriter("Mon profil", 0);
  }

  render() {
    return (<div id="moi">
      <h1 className="type-writer title"></h1>
    </div>);
  }
}

export default Moi;
