import React, {Component} from 'react';

import './Moi.css';

class Moi extends Component {

  constructor(props) {
    super(props);
    this.state = {
      message: ""
    }

    this.gerer_formulaire = this.gerer_formulaire.bind(this);
    this.valider = this.valider.bind(this);
  }

  sleep(t){
    var waitUntil = new Date().getTime() + t;
    var i = 0;
    while(new Date().getTime() < waitUntil) {
      i++;
    }
  }

  typeWriter(s, index) {
    let min = Math.ceil(300);
    let max = Math.floor(500);
    let vitesse = Math.floor(Math.random() * (max - min)) + min;
    if (index < s.length) {
      document.querySelector('.type-writer').innerHTML += '|';
      this.sleep(1000);
      this.setState({message : this.state.message + s.charAt(index)})
      //document.querySelector('.type-writer').innerHTML = document.querySelector('.type-writer').innerHTML.substring(0,document.querySelector('.type-writer').innerHTML.length-1);
      index++;
      setTimeout(() => {
        this.typeWriter(s, index, vitesse);
      }, vitesse);
    }
  }

  componentDidMount() {
    this.typeWriter("Mon profil", 0);
  }

  render() {
    return (<div id="moi">
      <h1 className="title">this.state.message</h1>
    </div>);
  }
}

export default Moi;
