import React, {Component} from 'react';

import './Moi.css';

class Moi extends Component {

  constructor(props) {
    super(props);
    this.state = {
      message: ""
    }
  }

  typeWriter(s, index) {
    let min = Math.ceil(300);
    let max = Math.floor(500);
    let vitesse = Math.floor(Math.random() * (max - min)) + min;
    if (index < s.length) {
      this.setState({message : this.state.message + s.charAt(index)});
      index++;
      setTimeout(() => {
        this.typeWriter(s, index, vitesse);
      }, vitesse);
    }
  }

  componentDidMount() {
    this.typeWriter("Mon profil", 0);
  }

  render() {
    return (<div id="moi">
      <h1 className="title">{this.state.message}</h1>
      <div class="box">
        <article class="media">
          <div class="media-left">
            <figure class="image is-64x64">
              <img src="../assets/informations_personnelles.PNG" alt="Image"/>
            </figure>
          </div>
          <div class="media-content">
            <div class="content">
              <p>
                <strong>LÃ©o ANDRE</strong> <small>leo</small> <small>31m</small>
                <br/>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Aenean efficitur sit amet massa fringilla egestas. Nullam condimentum luctus turpis.
              </p>
            </div>
          </div>
        </article>
      </div>
    </div>);
  }
}

export default Moi;
