import React, {Component} from 'react';

import './Moi.css';

class Moi extends Component {

  constructor(props) {
    super(props);
    this.state = {
      message: ""
    }
  }

  sleep(t){
    var waitUntil = new Date().getTime() + t;
    var i = 0;
    while(new Date().getTime() < waitUntil) {
      i++;
    }
  }

  typeWriter(s, index) {
    this.setState({message : this.state.message + '|'});
    this.sleep(200);
    this.setState({message : this.state.message.substring(0, this.state.message.length-1)});
    this.sleep(500);
    this.setState({message : this.state.message + s.charAt(index)});



    /*
    let min = Math.ceil(300);
    let max = Math.floor(500);
    let vitesse = Math.floor(Math.random() * (max - min)) + min;
    if (index < s.length) {
      this.setState({message : this.state.message + '|'});
      this.setState({message : this.state.message.substring(0, this.state.message.length-1)});
      this.setState({message : this.state.message + s.charAt(index)});
      index++;
      setTimeout(() => {
        this.typeWriter(s, index, vitesse);
      }, vitesse);
    }
    */
  }

  componentDidMount() {
    this.typeWriter("Mon profil", 0);
  }

  render() {
    return (<div id="moi">
      <h1 className="title">{this.state.message}</h1>
    </div>);
  }
}

export default Moi;
